cq:listeners.beforeedit:
function(comp){ 
	    				var response = CQ.HTTP.get(CQ.WCM.getPagePath() + '/_jcr_content/articleText.json');
						if(response.status == '200'){
							var responseText = JSON.parse(response.responseText);
							var dialog = CQ.WCM.getDialog(comp.dialog);
							dialog.getField('./articleText').originalValue=responseText.articleText;
							dialog.getField('./articleText').value=responseText.articleText;
							dialog.getField('./articleText').defaultValue=responseText.articleText;
							dialog.getField('./articleText').setValue(responseText.articleText);
							console.log(dialog.getField('./articleText'))
						}
			 		}
